import './Catalog.css';
import sakset from "../kuvat/sakset.jpg"
import React, { useState, useEffect } from "react";

/*
Jatkokehitys:
Tiedot tietokannasta, sijoitetaan vain alla olevaan pohjaan
Onnistuu tuomalla tuotteet kannasta taulukkoon (array?) jonka
tiedot näytetään näissä korteissa.

*/
const [isNavVisible, setNavVisibility] = useState(false);
const [isSmallScreen, setIsSmallScreen] = useState(false);


useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 1120px)");
    mediaQuery.addEventListener('change', handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeEventListener(handleMediaQueryChange);
    };
}, []);

const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
};

const toggleNav = () => {
    setNavVisibility(!isNavVisible);
};

export default function Catalog () {
	return (
        <div class="sivu">
            <Tuote/>
            <Tuote/>
        </div>
    
	
);
}  

const Tuote = () => {
    return (
        <div class="card"> 
        <img src={sakset} alt="sakset" id="sakset"/>
        <p><button>Tuote</button></p>
    </div>
    )
}
