import React, { useState, useEffect } from "react";
import "./Haku.css";
import { Link, Switch, Route, BrowserRouter as Router } from 'react-router-dom';

export default function Haku() {
  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  

  useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 1120px)");
    mediaQuery.addEventListener('change', handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeEventListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  return (
    <header className="Header">
      <a href="/nyyppaguide"><img src={logo} className="Logo" alt="logo"/></a>
      {(!isSmallScreen || isNavVisible) && (
        <nav className="Nav">
        <Link to="/cooking">Cooking</Link>
		<Link to="/alchemy">Alchemy</Link>
        <Link to="/gathering">Gathering</Link>
        <Link to="/farming">Farming</Link>
        <Link to="/training">Training</Link>
        <Link to="/sailing">Sailing</Link>
        <Link to="/laskurit">Laskurit</Link>
        <Link to="/yhteys">Ota yhteyttä</Link>
        </nav> )}
      <button onClick={toggleNav} className="Burger">☰</button>
    </header>
  );
}
